mapper.sql:

/* (3) */
WHERE
    todo_title LIKE #{criteria.title} || '%' ESCAPE '~'
AND
    created_at < #{criteria.createdAt}

mapper.delete:
DELETE FROM
t_todo
WHERE
todo_id = #{todoId}
AND
version = #{version}

mapper.select:
SELECT todo_id, todo_title, finished, created_at, version FROM t_todo WHERE todo_title LIKE #{title} || '%' ESCAPE '~'
AND finished = #{finished}
ORDER BY todo_id

SELECT todo_id, todo_title, finished, created_at, complete_at, version FROM t_todo
todo_title LIKE #{titleContainingCondition} ESCAPE '~'
AND created_at < #{createdAt}

SELECT todo_id, todo_title, category_id, finished, created_at, version FROM t_todo
todo_title LIKE #{titleContainingCondition} ESCAPE '~'
AND created_at < #{createdAt}

SELECT todo_id, todo_title, category_id, finished, created_at, version FROM t_todo
todo_title LIKE #{titleContainingCondition} ESCAPE '~'
AND created_at < #{createdAt}
